import chalk from 'chalk';

const joke = 'Han var det tak i sa kjerringa da hun fødte en sønn med håndtak';

const words = joke.split(' ');

const colors = [chalk.red, chalk.blue, chalk.magenta, chalk.green];

let colorIndex = 0;

words.forEach((word) => {
  const currentColor = colors[colorIndex % colors.length];
  process.stdout.write(currentColor(word) + ' ');
  colorIndex++;
});

console.log();

